/**
File: lab1_ij_segControl.sv
Author: Isaiah Jeter
Contact Info: ijeter@g.hmc.edu
Last Updated: 09/02/25
Summary: Control module for seven segment display 
*/
module segControl (
    input logic [3:0] switches,       
    output logic [6:0] seg       
);

    always_comb begin
        case (switches)
            4'h0: seg = 7'b1000000;
            4'h1: seg = 7'b1111001;
            4'h2: seg = 7'b0100100; 
            4'h3: seg = 7'b0110000;
            4'h4: seg = 7'b0011001; 
            4'h5: seg = 7'b0010010;
            4'h6: seg = 7'b0000010;
            4'h7: seg = 7'b1111000; 
            4'h8: seg = 7'b0000000; 
            4'h9: seg = 7'b0010000; 
            4'hA: seg = 7'b0001000; 
            4'hB: seg = 7'b0000011; 
            4'hC: seg = 7'b1000110; 
            4'hD: seg = 7'b0100001; 
            4'hE: seg = 7'b0000110; 
            4'hF: seg = 7'b0001110; 
            default: seg = 7'b1111111; 

        endcase
    end

endmodule

lab1_ij_ledControl.sv
/**
File: lab1_ij_ledControl.sv
Author: Isaiah Jeter
Contact Info: ijeter@g.hmc.edu
Last Updated: 09/02/25
Summary: Control module for logic LEDs 
*/
module ledControl (
    input logic [3:0] switches,      // 4 DIP switches [3:0]
    output logic [2:0] led       // 3 LED outputs [2:0]
);
    logic int_osc;
    logic [24:0] counter;
    logic toggle;

    HSOSC #(.CLKHF_DIV(2'b01)) 
         hf_osc (.CLKHFPU(1'b1), .CLKHFEN(1'b1), .CLKHF(int_osc));

   always_ff @(posedge int_osc) begin
     if(counter &gt;= 25'd19999999)  begin
         toggle &lt;= ~toggle;
         counter &lt;= 0;
         end
     else            counter &lt;= counter + 25'd1;
   end

    // LED control logic
    logic led0, led1;

    assign led0 = switches[1] ^ switches[0];
    assign led1 = switches[3] & switches[2];

    assign led[2] = toggle;
    assign led[1] = led1;
    assign led[0] = led0;

endmodule
